import React, {useState} from "react";
import "../index.css"

const ExpenseForm = () => {

    const [enteredTitle, setTitle] = useState('')
    const [enteredAmount, setAmount] = useState('')
    const [enteredDate, setDate] = useState('')

    const enteredTitleHandler = (event) => {
        console.log(event.target.value)
    }

    const enteredAmountHandler = (event) => {
        console.log(event.target.value)
    }

    const enteredDateHandler = (event) => {
        console.log(event.target.value)
    }

    return(

        <div className="wrapper">
            <form>
                <div className="row">
                    <div className="col-md-6">
                        <label>Title</label>
                        <br />
                        <input type="text" onChange={enteredTitleHandler}/>
                    </div>
                    <div className="col-md-6">
                        <label>Amount</label>
                        <br />
                        <input type="number" min="0.01" step="0.01" onChange={enteredAmountHandler}/>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-6">
                        <label>Date</label>
                        <br />
                        <input type="date" min="2019-01-01" max="2022-08-11" onChange={enteredDateHandler}/>
                    </div>
                </div>
                <div>
                    <button className="btn" on>Add Expense</button>
                </div>
            </form>
        </div>
    )
}

export default ExpenseForm
