import React, {useState} from "react";
import "../index.css"

const ExpenseForm = () => {

    const [enteredTitle, setTitle] = useState('')
    const [enteredAmount, setAmount] = useState('')
    const [enteredDate, setDate] = useState('')

    const submitHandler = (event) => {
        event.preventDefault()

        const expenseData = {
            title : enteredTitle,
            amount : enteredAmount,
            date : new Date(enteredDate) 
        }

        console.log(expenseData)
    }

    const enteredTitleHandler = (event) => {
        setTitle(event.target.value)
    }

    const enteredAmountHandler = (event) => {
        setAmount(event.target.value)
    }

    const enteredDateHandler = (event) => {
        setDate(event.target.value)
    }

    return(

        <div className="wrapper">
            <form onSubmit={submitHandler}>
                <div className="row">
                    <div className="col-md-6">
                        <label>Title</label>
                        <br />
                        <input type="text" value={enteredTitle} onChange={enteredTitleHandler}/>
                    </div>
                    <div className="col-md-6">
                        <label>Amount</label>
                        <br />
                        <input type="number" min="0.01" step="0.01" value={enteredAmount} onChange={enteredAmountHandler}/>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-6">
                        <label>Date</label>
                        <br />
                        <input type="date" min="2019-01-01" max="2022-08-11" value={entered} onChange={enteredDateHandler}/>
                    </div>
                </div>
                <div>
                    <button className="btn">Add Expense</button>
                </div>
            </form>
        </div>
    )
}

export default ExpenseForm
