import React from "react";
import "./ChartBar.css"

const ChartBar = (props) => {
    let barHeight = '0%'

    if(props.maxValue > 0){
        barHeight = Math.round(props.value / props.maxValue * 100) + '%'
    }

    
  
}

export default ChartBar
